var myMap,popupbtn=document.querySelector(".popup-button"),popup=document.querySelector(".hotels-form"),input1=popup.querySelector("#arrival"),input2=popup.querySelector("#departure"),submitbtn=popup.querySelector(".search-button");function init(){myMap=new ymaps.Map("map",{center:[34.87195183267487,-111.756269],zoom:10},{searchControlProvider:"yandex#search"}),myPlacemark=new ymaps.Placemark(myMap.getCenter()),myMap.geoObjects.add(myPlacemark),myPlacemark.events.add("mouseenter",function(e){e.get("target").options.set("preset","islands#greenIcon")}).add("mouseleave",function(e){e.get("target").options.unset("preset")})}popup.classList.remove("show-popup"),popupbtn.addEventListener("click",function(e){e.preventDefault(),popup.classList.toggle("show-popup"),popup.classList.remove("modal-error")}),submitbtn.addEventListener("click",function(e){input1.value&&input2.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),ymaps.ready(init);
